DECLARE @Ngay varchar(100) = N'{ngay_Vao}'

SELECT * FROM 
(SELECT 
        MA_LK =         
            CASE 
                WHEN lba.Dictionary_Name_En = N'Ngoại trú' THEN tn.SoTiepNhan
                WHEN lba.Dictionary_Name_En IN (N'Nội ngày', N'Nội trú') THEN ba.SoBenhAn
