            END,
        MaMay = mamay.Dictionary_Code,
        ThoiGian = kq.ThoiGianThucHien
FROM 
(
    SELECT ThietBi_Id, ThoiGianThucHien
    FROM CLSKetQua
    WHERE ThietBi_Id IS NOT NULL
    GROUP BY ThietBi_Id, ThoiGianThucHien
    HAVING COUNT(*) > 1
) t 
