LEFT JOIN XacNhanChiPhiChiTiet xnct ON clsyc.YeuCauChiTiet_Id = xnct.IDRef AND xnct.Loai_IDRef = 'A'
LEFT JOIN XacNhanChiPhi Xn ON xn.XacNhanChiPhi_Id = xnct.XacNhanChiPhi_Id
LEFT JOIN BenhAn ba ON ba.BenhAn_Id = xn.BenhAn_Id
LEFT JOIN TiepNhan tn ON tn.TiepNhan_Id = xn.TiepNhan_Id
